    public static int shortestPath(int[][] grid, int k) {
        List<int[]> paths = new ArrayList<>();
        boolean[][] visited = new boolean[grid.length][grid[0].length];

        Queue<int[]> queue = new LinkedList<>();
        queue.offer(new int[]{0,0,k,0}); //row,col,obstaclesLimit,distancefrom00

        while (!queue.isEmpty()){

            int size = queue.size();
            for (int i = 0; i < size; i++) {

                int[] currentElement = queue.poll();
                int row = currentElement[0];
                int col = currentElement[1];
                if(row<0 || row>= grid.length || col<0 || col>=grid[0].length)
                    continue;
                int obstacleLeft = currentElement[2];
                int distance = currentElement[3];
                if(row==grid.length && col == grid[0].length)
                    return distance;
                if(visited[row][col])
                    continue;
                if(grid[row][col] == 1){
                    if(--obstacleLeft < 0)
                        continue;
                }
                distance++;


            }

        }

        return 0;

    }




